                            </label>
                            <div id="card-element">
                            </div>

                            <div id="card-errors" role="alert"></div>
                        </div>

                        <button>Submit Payment</button>
                    </form>
                </main>

            </div>
        )
    }
}



export default injectStripe(Form);
